import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Ensure this is imported to use navigation
import './styles.css';  // Ensure your CSS styles are linked correctly

function Login() {
    const [emailOrPhone, setEmailOrPhone] = useState('');
    const [password, setPassword] = useState('');
    const [submitted, setSubmitted] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Hook for navigating to other routes

    // Function to handle input changes and update state accordingly
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name === 'emailOrPhone') {
            setEmailOrPhone(value);
        } else if (name === 'password') {
            setPassword(value);
        }
    };

    // Regex to validate email or phone number
    const isValidEmailOrPhone = (input) => {
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;  // Simple email pattern
        const phonePattern = /^\+1[0-9]{10}$/;  // US phone number pattern with country code
        return emailPattern.test(input) || phonePattern.test(input);
    };

    // Function to handle form submission
    const handleLogin = async (e) => {
        e.preventDefault();
        setSubmitted(true); // Mark the form as submitted

        if (!emailOrPhone || !password || !isValidEmailOrPhone(emailOrPhone)) {
            setError('Please enter a valid email or phone number and password.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:8000/login', {
                username: emailOrPhone,
                password: password
            });
            console.log(response.data); // Log the server response
            navigate('/dashboard'); // Redirect to dashboard or home page after successful login
        } catch (error) {
            console.error('Login failed', error);
            setError('Failed to login. Please check your credentials.');
        }
    };

    return (
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Log in to PayPal</h2>
                <div className="input-container">
                    <input
                        type="text"
                        name="emailOrPhone"
                        value={emailOrPhone}
                        onChange={handleInputChange}
                        placeholder="Email or mobile number"
                        className={submitted && !isValidEmailOrPhone(emailOrPhone) ? 'input-error' : ''}
                    />
                    <input
                        type="password"
                        name="password"
                        value={password}
                        onChange={handleInputChange}
                        placeholder="Password"
                        className={submitted && !password ? 'input-error' : ''}
                    />
                </div>
                {error && <div className="error-message">{error}</div>}
                <button type="submit" className="login-button">Login</button>
                <div className="login-options">
                    <button type="button" onClick={() => alert('Redirect to forgot email')} className="forgot-email-button">Forgot email?</button>
                    <button type="button" onClick={() => navigate('/signup')} className="signup-button">Sign Up</button>
                </div>
            </form>
        </div>
    );
}

export default Login;
