import React, { useState } from 'react';
import axios from 'axios';

import './styles.css'; // Make sure to include appropriate CSS

function Login() {
    const [emailOrPhone, setEmailOrPhone] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // Simulating the next step after email/phone submission
            const response = await axios.post('http://localhost:8000/verify', { emailOrPhone });
            alert('Proceed to password entry'); // Normally, you'd redirect to another form here
        } catch (error) {
            alert('Failed to verify email or phone');
        }
    };

    return (
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Log in to PayPal</h2>
                <input 
                    type="text" 
                    value={emailOrPhone} 
                    onChange={e => setEmailOrPhone(e.target.value)} 
                    placeholder="Email or mobile number" 
                    className="login-input"
                />
                <div className="login-actions">
                    <button type="submit" className="login-button">Next</button>
                    <button type="button" onClick={() => alert('Go to sign up')} className="signup-button">Sign Up</button>
                </div>
                <a href="#" onClick={() => alert('Redirect to forgot email')}>Forgot email?</a>
            </form>
            <footer className="login-footer">
                <img src="/path/to/paypal-logo.png" alt="PayPal Logo" />
            </footer>
        </div>
    );
}

export default Login;
