import React, { useState } from 'react';
import './Login.css'; // Ensure the CSS is properly linked

function Login() {
    const [emailOrPhone, setEmailOrPhone] = useState('');
    const [submitted, setSubmitted] = useState(false); // Tracks if the form has been submitted

    const handleLogin = async (e) => {
        e.preventDefault();
        setSubmitted(true); // Mark the form as submitted to trigger validation

        if (!emailOrPhone || (isPhoneNumber(emailOrPhone) && emailOrPhone.length !== 12)) {
            // Validate if input is empty or not a properly formatted phone number
            return;
        }

        try {
            // Here, integrate your backend logic
            alert('Proceed to password entry'); // Placeholder for successful validation
        } catch (error) {
            alert('Failed to verify email or phone');
        }
    };

    const isPhoneNumber = (input) => {
        // Regex to check if the input is a valid US phone number with country code +1
        return /^\+1[0-9]{10}$/.test(input);
    };

    return (
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Log in to PayPal</h2>
                <div className="input-container">
                    <input
                        type="text"
                        value={emailOrPhone}
                        onChange={e => setEmailOrPhone(e.target.value)}
                        placeholder="Email or mobile number"
                        className={submitted && (!emailOrPhone || (isPhoneNumber(emailOrPhone) && emailOrPhone.length !== 12)) ? 'input-error' : ''}
                    />
                    {isPhoneNumber(emailOrPhone) && <span className="country-code">+1</span>}
                </div>
                {submitted && (!emailOrPhone || (isPhoneNumber(emailOrPhone) && emailOrPhone.length !== 12)) && 
                    <div className="error-message">{emailOrPhone && !isPhoneNumber(emailOrPhone) ? "Invalid phone number format" : "*Required"}</div>
                }
                <button type="submit" className="login-button">Next</button>
                <div className="login-options">
                    <button type="button" onClick={() => alert('Redirect to forgot email')} className="forgot-email-button">Forgot email?</button>
                    <button type="button" onClick={() => alert('Go to sign up')} className="signup-button">Sign Up</button>
                </div>
            </form>
        </div>
    );
}

export default Login;
