import React, { useState } from 'react';
import './styles.css'; // Make sure your styles are imported

function Login() {
    const [emailOrPhone, setEmailOrPhone] = useState('');
    const [submitted, setSubmitted] = useState(false); // State to track whether the form has been submitted

    const handleLogin = async (e) => {
        e.preventDefault();
        setSubmitted(true); // Set the submitted state to true when the form is submitted

        if (!emailOrPhone || (isPhoneNumber(emailOrPhone) && emailOrPhone.length !== 12)) {
            // If email or phone is not entered or phone number length is not 12 including country code
            return;
        }

        try {
            // Placeholder for your actual submission logic
            alert('Proceed to password entry'); // Simulate successful submission
        } catch (error) {
            alert('Failed to verify email or phone');
        }
    };

    const isPhoneNumber = (input) => {
        return /^\+1[0-9]{10}$/.test(input);
    };

    return (
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Log in to PayPal</h2>
                <div className="input-container">
                    <input
                        type="text"
                        value={emailOrPhone}
                        onChange={e => setEmailOrPhone(e.target.value)}
                        placeholder="Email or mobile number"
                        className={submitted && (!emailOrPhone || (isPhoneNumber(emailOrPhone) && emailOrPhone.length !== 12)) ? 'input-error' : ''}
                    />
                    {isPhoneNumber(emailOrPhone) && <span className="country-code">+1</span>}
                </div>
                {submitted && (!emailOrPhone || (isPhoneNumber(emailOrPhone) && emailOrPhone.length !== 12)) && 
                    <div className="error-message">{emailOrPhone && !isPhoneNumber(emailOrPhone) ? "Invalid phone number format" : "*Required"}</div>
                }
                <button type="submit" className="login-button">Next</button>
            </form>
        </div>
    );
}

export default Login;
