import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import './Styles.css'; // Ensure CSS is properly linked

function SignUp() {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        phoneNumber: '',
        email: '',
        password: '',
        streetAddress: '',
        apt: '',
        city: '',
        state: '',
        zipCode: ''
    });
    const history = useHistory(); // Hook to access the history instance

    // Function to update state based on form input changes
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    // Function to handle form submission for registration
    const handleSignUp = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8000/signup', formData);
            console.log("Signup successful", response.data);
            alert("Registration Successful!");
        } catch (error) {
            console.error('Failed to register', error);
            alert("Registration Failed!");
        }
    };

    return (
        <div>
            <form onSubmit={handleSignUp}>
                {/* Form inputs */}
                <button type="submit">Sign Up</button>
            </form>
            <button onClick={() => history.push('/login')} className="link-button">Already have an account? Log In</button>
        </div>
    );
}

export default SignUp;
